import org.maplibre.android.plugins.annotation.SymbolManager
import org.maplibre.android.plugins.annotation.SymbolOptions

// Define 3 states for the bottom sheet
enum class SheetState { COLLAPSED, HALF_EXPANDED, FULLY_EXPANDED }

@OptIn(ExperimentalPermissionsApi::class)
@Composable
fun MapScreen(
    currentLocation: Location?,
    nearbySecrets: List<Secret>,
    onSecretClick: (Secret) -> Unit,
    onSheetStateChange: (String) -> Unit = {},
    onLocationPermissionGranted: () -> Unit = {}
) {
    var showSecretPreview by remember { mutableStateOf<Secret?>(null) }
    var focusedSecret by remember { mutableStateOf<Secret?>(null) }

    // Location permissions state
    val locationPermissions = rememberMultiplePermissionsState(
        permissions = listOf(
            Manifest.permission.ACCESS_FINE_LOCATION,
            Manifest.permission.ACCESS_COARSE_LOCATION
        )
    )

    // Update location when permission is granted
    LaunchedEffect(locationPermissions.allPermissionsGranted) {
        if (locationPermissions.allPermissionsGranted && currentLocation == null) {
            onLocationPermissionGranted()
        }
    }

    Box(modifier = Modifier.fillMaxSize()) {
        // Map View - Only show if permissions are granted
        if (locationPermissions.allPermissionsGranted) {
            // Show map if we have location OR show loading if location is being fetched
            if (currentLocation != null) {
                MapViewComposable(
                    currentLocation = currentLocation,
                    secrets = nearbySecrets,
                    onMarkerClick = { showSecretPreview = it },
                    focusedSecret = focusedSecret,
                    onFocusHandled = { focusedSecret = null }
                )
            } else {
                // Permissions granted but location still loading
                Box(
                    modifier = Modifier.fillMaxSize(),
                    contentAlignment = Alignment.Center
                ) {
                    androidx.compose.foundation.Image(
                        painter = painterResource(id = com.secretspaces32.android.R.drawable.map_placeholder),
                        contentDescription = "Map Placeholder",
                        modifier = Modifier.fillMaxSize(),
                        contentScale = ContentScale.Crop
                    )

                    Box(
                        modifier = Modifier
                            .fillMaxSize()
                            .background(Color.Black.copy(alpha = 0.5f))
                    )

                    Column(
                        horizontalAlignment = Alignment.CenterHorizontally,
                        verticalArrangement = Arrangement.spacedBy(12.dp)
                    ) {
                        CircularProgressIndicator(
                            color = TealPrimary,
                            modifier = Modifier.size(42.dp)
                        )
                        Text(
                            text = "Getting your location...",
                            color = Color.White,
                            style = MaterialTheme.typography.bodyLarge,
                            fontWeight = FontWeight.SemiBold
                        )
                    }
                }
            }
        } else {
            // Show map placeholder with location permission request
            Box(
                modifier = Modifier.fillMaxSize(),
                contentAlignment = Alignment.Center
            ) {
                // Map placeholder image
                androidx.compose.foundation.Image(
                    painter = painterResource(id = com.secretspaces32.android.R.drawable.map_placeholder),
                    contentDescription = "Map Placeholder",
                    modifier = Modifier.fillMaxSize(),
                    contentScale = ContentScale.Crop
                )

                // Dark overlay for better text visibility
                Box(
                    modifier = Modifier
                        .fillMaxSize()
                        .background(Color.Black.copy(alpha = 0.5f))
                )

                // Permission request UI
                Column(
                    horizontalAlignment = Alignment.CenterHorizontally,
                    verticalArrangement = Arrangement.spacedBy(16.dp),
                    modifier = Modifier.padding(32.dp)
                ) {
                    // Map icon
                    Icon(
                        imageVector = Icons.Default.LocationOn,
                        contentDescription = "Location",
                        tint = TealPrimary,
                        modifier = Modifier.size(80.dp)
                    )

                    Text(
                        text = "Enable Location",
                        color = Color.White,
                        style = MaterialTheme.typography.headlineSmall,
                        fontWeight = FontWeight.Bold
                    )

                    Text(
                        text = "To view nearby secrets on the map, we need access to your location.",
                        color = Color.White.copy(alpha = 0.9f),
                        style = MaterialTheme.typography.bodyMedium,
                        textAlign = TextAlign.Center,
                        lineHeight = 20.sp
                    )

                    Spacer(modifier = Modifier.height(8.dp))

                    // Permission request button
                    Button(
                        onClick = {
                            locationPermissions.launchMultiplePermissionRequest()
                        },
                        colors = ButtonDefaults.buttonColors(
                            containerColor = TealPrimary
                        ),
                        shape = RoundedCornerShape(12.dp),
                        modifier = Modifier
                            .fillMaxWidth(0.8f)
                            .height(56.dp)
                    ) {
                        Icon(
                            imageVector = Icons.Default.MyLocation,
                            contentDescription = null,
                            modifier = Modifier.size(24.dp)
                        )
                        Spacer(modifier = Modifier.width(8.dp))
                        Text(
                            text = "Allow Location Access",
                            fontSize = 16.sp,
                            fontWeight = FontWeight.SemiBold
                        )
                    }

                    // Show different message if permission was denied
                    if (locationPermissions.permissions.any { !it.status.isGranted } && !locationPermissions.allPermissionsGranted) {
                        Text(
                            text = "Location permission is required to show the map. Please grant permission in the dialog above.",
                            color = Color.White.copy(alpha = 0.7f),
                            style = MaterialTheme.typography.bodySmall,
                            textAlign = TextAlign.Center,
                            lineHeight = 18.sp
                        )
                    }
                }
            }
        }

        // Secret Preview Modal
        AnimatedVisibility(
            visible = showSecretPreview != null,
            enter = fadeIn() + scaleIn(initialScale = 0.8f),
            exit = fadeOut() + scaleOut(targetScale = 0.8f)
        ) {
            showSecretPreview?.let { secret ->
                Box(
                    modifier = Modifier
                        .fillMaxSize()
                        .background(Color.Black.copy(alpha = 0.6f))
                        .clickable { showSecretPreview = null },
                    contentAlignment = Alignment.Center
                ) {
                    Card(
                        modifier = Modifier.fillMaxWidth(0.9f),
                        shape = RoundedCornerShape(24.dp),
                        colors = CardDefaults.cardColors(containerColor = DarkSurface),
                        elevation = CardDefaults.cardElevation(10.dp)
                    ) {
                        Column(modifier = Modifier.padding(24.dp)) {
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Text(
                                    text = " Secret",
                                    style = MaterialTheme.typography.titleLarge,
                                    color = TealPrimary,
                                    fontWeight = FontWeight.Bold
                                )
                                IconButton(
                                    onClick = { showSecretPreview = null },
                                    modifier = Modifier.size(32.dp)
                                ) {
                                    Icon(
                                        imageVector = Icons.Default.Close,
                                        contentDescription = "Close",
                                        tint = Color.White.copy(alpha = 0.7f)
                                    )
                                }
                            }

                            Spacer(modifier = Modifier.height(16.dp))
                            Text(
                                text = secret.text,
                                style = MaterialTheme.typography.bodyLarge,
                                color = Color.White,
                                lineHeight = 24.sp
                            )

                            Spacer(modifier = Modifier.height(20.dp))

                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                secret.distance?.let { distance ->
                                    Row(
                                        horizontalArrangement = Arrangement.spacedBy(6.dp),
                                        verticalAlignment = Alignment.CenterVertically
                                    ) {
                                        Icon(
                                            imageVector = Icons.Default.LocationOn,
                                            contentDescription = null,
                                            tint = AquaGreen,
                                            modifier = Modifier.size(18.dp)
                                        )
                                        Text(
                                            text = LocationHelper.formatDistance(distance),
                                            style = MaterialTheme.typography.bodyMedium,
                                            color = AquaGreen
                                        )
                                    }
                                }

                                Button(
                                    onClick = {
                                        showSecretPreview = null
                                        onSecretClick(secret)
                                    },
                                    colors = ButtonDefaults.buttonColors(containerColor = TealPrimary),
                                    shape = RoundedCornerShape(12.dp)
                                ) {
                                    Text("View Details")
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}


@Composable
fun MapViewComposable(
    currentLocation: Location,
    secrets: List<Secret>,
    onMarkerClick: (Secret) -> Unit,
    focusedSecret: Secret? = null,
    onFocusHandled: () -> Unit = {}
) {
    val lifecycleOwner = LocalLifecycleOwner.current
    val lifecycle = lifecycleOwner.lifecycle

    var mapView by remember { mutableStateOf<MapView?>(null) }
    var symbolManager by remember { mutableStateOf<SymbolManager?>(null) }
    var isMapReady by remember { mutableStateOf(false) }
    var mapLibreMapInstance by remember { mutableStateOf<org.maplibre.android.maps.MapLibreMap?>(null) }

    // Handle focusing on a specific secret when requested
    LaunchedEffect(focusedSecret) {
        focusedSecret?.let { secret ->
            mapLibreMapInstance?.let { map ->
                try {
                    val secretPosition = LatLng(secret.latitude, secret.longitude)
                    map.animateCamera(
                        CameraUpdateFactory.newLatLngZoom(secretPosition, 16.0),
                        1500
                    )
                    onFocusHandled()
                } catch (e: Exception) {
                    onFocusHandled()
                }
            }
        }
    }

    DisposableEffect(Unit) {
        onDispose {
            try {
                symbolManager?.onDestroy()
                mapView?.onPause()
                mapView?.onStop()
                mapView?.onDestroy()
            } catch (_: Exception) {
            }
            symbolManager = null
            mapView = null
        }
    }

    Box(modifier = Modifier.fillMaxSize().background(DarkBackground)) {
        AndroidView(
            factory = { ctx ->
                try {

                    // Initialize MapLibre BEFORE creating MapView
                    try {
                        MapLibre.getInstance(ctx)
                    } catch (_: Exception) {
                        // MapLibre not initialized, initialize it now
                        MapLibre.getInstance(ctx)
                    }

                    MapView(ctx).apply {
                        this.id = android.view.View.generateViewId()
                        setBackgroundColor(android.graphics.Color.BLACK)

                        // Store reference immediately
                        mapView = this

                        // Initialize lifecycle immediately in factory
                        this.onCreate(null)
                        this.onStart()
                        this.onResume()

                        // Setup map directly in factory using post to ensure view is attached
                        this.post {
                            try {
                                this.getMapAsync { mapLibreMap ->
                                    // Store the map instance for camera control
                                    mapLibreMapInstance = mapLibreMap
                                    try {
                                        val styleUrl = "https://api.maptiler.com/maps/streets-v2/style.json?key=${BuildConfig.MAPTILER_API_KEY}"

                                        mapLibreMap.setStyle(styleUrl) { style ->
                                            try {
                                                // Set initial camera position to user's location
                                                mapLibreMap.moveCamera(
                                                    CameraUpdateFactory.newLatLngZoom(
                                                        LatLng(currentLocation.latitude, currentLocation.longitude),
                                                        14.0
                                                    )
                                                )

                                                // Clean up old symbol manager
                                                symbolManager?.onDestroy()

                                                // Create symbol manager
                                                val newSymbolManager = SymbolManager(this, mapLibreMap, style)
                                                newSymbolManager.iconAllowOverlap = true
                                                newSymbolManager.textAllowOverlap = true
                                                symbolManager = newSymbolManager

                                                // Current location marker
                                                newSymbolManager.create(
                                                    SymbolOptions()
                                                        .withLatLng(LatLng(currentLocation.latitude, currentLocation.longitude))
                                                        .withTextField("")
                                                        .withTextSize(18f)
                                                        .withTextColor("rgb(0, 217, 208)")
                                                )

                                                // Secret markers
                                                secrets.forEach { secret ->
                                                    newSymbolManager.create(
                                                        SymbolOptions()
                                                            .withLatLng(LatLng(secret.latitude, secret.longitude))
                                                            .withTextField("")
                                                            .withTextSize(18f)
                                                            .withTextColor("rgb(123, 97, 255)")
                                                            .withData(JsonPrimitive(secret.id))
                                                    )
                                                }

                                                newSymbolManager.addClickListener { symbol ->
                                                    symbol.data?.let { data ->
                                                        secrets.find { it.id == data.asString }?.let(onMarkerClick)
                                                    }
                                                    true
                                                }

                                                isMapReady = true
                                            } catch (e: Exception) {
                                                isMapReady = true
                                            }
                                        }
                                    } catch (e: Exception) {
                                        isMapReady = true
                                    }
                                }
                            } catch (e: Exception) {
                                isMapReady = true
                            }
                        }
                    }
                } catch (e: Exception) {
                    // Return a fallback view if map creation fails
                    android.widget.FrameLayout(ctx).apply {
                        setBackgroundColor(android.graphics.Color.BLACK)
                    }
                }
            },
            modifier = Modifier.fillMaxSize()

        )

        // Show loading indicator while map initializes
        if (!isMapReady) {
            Box(
                modifier = Modifier.fillMaxSize(),
                contentAlignment = Alignment.Center
            ) {
                Column(
                    horizontalAlignment = Alignment.CenterHorizontally,
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    CircularProgressIndicator(color = TealPrimary)
                    Text(
                        text = "Initializing map...",
                        color = Color.White,
                        style = MaterialTheme.typography.bodyMedium
                    )
                }
            }
        }
    }

    // Timeout fallback
    LaunchedEffect(Unit) {
        delay(15000)
        if (!isMapReady) {
            isMapReady = true
        }
    }

    DisposableEffect(lifecycle) {
        val observer = LifecycleEventObserver { _, event ->
            when (event) {
                Lifecycle.Event.ON_PAUSE -> {
                    try {
                        mapView?.onPause()
                    } catch (_: Exception) {
                    }
                }
                Lifecycle.Event.ON_RESUME -> {
                    try {
                        mapView?.onResume()
                    } catch (_: Exception) {
                    }
                }
                Lifecycle.Event.ON_STOP -> {
                    try {
                        mapView?.onStop()
                    } catch (_: Exception) {
                    }
                }
                Lifecycle.Event.ON_START -> {
                    try {
                        mapView?.onStart()
                    } catch (_: Exception) {
                    }
                }
                Lifecycle.Event.ON_DESTROY -> {
                    try {
                        symbolManager?.onDestroy()
                        symbolManager = null
                        mapView?.onDestroy()
                        mapView = null
                    } catch (_: Exception) {
                    }
                }
                else -> {}
            }
        }

        lifecycle.addObserver(observer)

        onDispose {
            lifecycle.removeObserver(observer)
        }
    }
}

package com.secretspaces32.android.viewmodel

import android.content.Context
import android.location.Location
import android.net.Uri
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.secretspaces32.android.data.model.Comment
import com.secretspaces32.android.data.model.Like
import com.secretspaces32.android.data.model.Secret
import com.secretspaces32.android.data.model.User
import com.secretspaces32.android.data.model.UpdateUserRequest
import com.secretspaces32.android.data.repository.AuthRepository
import com.secretspaces32.android.data.repository.FirebaseSecretRepository
import com.secretspaces32.android.data.repository.FirebaseUserRepository
import com.secretspaces32.android.utils.LocationHelper
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch

data class AppUiState(
    val isAuthenticated: Boolean = false,
    val currentUser: User? = null,
    val currentLocation: Location? = null,
    val secrets: List<Secret> = emptyList(),
    val mySecrets: List<Secret> = emptyList(),
    val selectedSecret: Secret? = null,
    val selectedSecretComments: List<Comment> = emptyList(),
    val selectedSecretLikes: List<Like> = emptyList(),
    val isLoading: Boolean = false,
    val isEmailAuthLoading: Boolean = false,
    val isGoogleAuthLoading: Boolean = false,
    val errorMessage: String? = null
)

class MainViewModel(
    private val context: Context
) : ViewModel() {

    private val authRepository = AuthRepository()
    private val userRepository = FirebaseUserRepository()
    private val secretRepository = FirebaseSecretRepository()
    private val locationHelper = LocationHelper(context)

    private val _uiState = MutableStateFlow(AppUiState())
    val uiState: StateFlow<AppUiState> = _uiState.asStateFlow()

    init {
        checkAuthState()
    }

    private fun checkAuthState() {
        viewModelScope.launch {
            authRepository.currentUser.collect { firebaseUser ->
                if (firebaseUser != null) {
                    loadCurrentUser(firebaseUser.uid)
                } else {
                    _uiState.value = _uiState.value.copy(
                        isAuthenticated = false,
                        currentUser = null
                    )
                }
            }
        }
    }

    private suspend fun loadCurrentUser(userId: String) {
        val result = userRepository.getUser(userId)
        result.onSuccess { user ->
            _uiState.value = _uiState.value.copy(
                isAuthenticated = true,
                currentUser = user
            )
        }.onFailure {
            _uiState.value = _uiState.value.copy(
                isAuthenticated = true,
                currentUser = null,
                errorMessage = "Failed to load user profile"
            )
        }
    }

    fun signUp(email: String, password: String, username: String) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isEmailAuthLoading = true)

            val authResult = authRepository.signUp(email, password, username)
            authResult.onSuccess { firebaseUser ->
                val userResult = userRepository.createUser(
                    userId = firebaseUser.uid,
                    email = email,
                    username = username
                )

                userResult.onSuccess { user ->
                    _uiState.value = _uiState.value.copy(
                        isAuthenticated = true,
                        currentUser = user,
                        isEmailAuthLoading = false
                    )
                }.onFailure { e ->
                    _uiState.value = _uiState.value.copy(
                        isEmailAuthLoading = false,
                        errorMessage = "Failed to create user profile: ${e.message}"
                    )
                }
            }.onFailure { e ->
                _uiState.value = _uiState.value.copy(
                    isEmailAuthLoading = false,
                    errorMessage = "Sign up failed: ${e.message}"
                )
            }
        }
    }

    fun signIn(email: String, password: String) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isEmailAuthLoading = true)

            val result = authRepository.signIn(email, password)
            result.onSuccess {
                _uiState.value = _uiState.value.copy(isEmailAuthLoading = false)
            }.onFailure { e ->
                _uiState.value = _uiState.value.copy(
                    isEmailAuthLoading = false,
                    errorMessage = "Sign in failed: ${e.message}"
                )
            }
        }
    }

    fun signInWithGoogle(idToken: String) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isGoogleAuthLoading = true)

            val authResult = authRepository.signInWithGoogle(idToken)
            authResult.onSuccess { firebaseUser ->
                // Check if user exists in Firestore
                val userResult = userRepository.getUser(firebaseUser.uid)

                userResult.onSuccess { user ->
                    // User already exists
                    _uiState.value = _uiState.value.copy(
                        isAuthenticated = true,
                        currentUser = user,
                        isGoogleAuthLoading = false
                    )
                }.onFailure {
                    // Create new user profile
                    val createResult = userRepository.createUser(
                        userId = firebaseUser.uid,
                        email = firebaseUser.email ?: "",
                        username = firebaseUser.displayName ?: "User"
                    )

                    createResult.onSuccess { user ->
                        _uiState.value = _uiState.value.copy(
                            isAuthenticated = true,
                            currentUser = user,
                            isGoogleAuthLoading = false
                        )
                    }.onFailure { e ->
                        _uiState.value = _uiState.value.copy(
                            isGoogleAuthLoading = false,
                            errorMessage = "Failed to create user profile: ${e.message}"
                        )
                    }
                }
            }.onFailure { e ->
                _uiState.value = _uiState.value.copy(
                    isGoogleAuthLoading = false,
                    errorMessage = "Google sign in failed: ${e.message}"
                )
            }
        }
    }

    fun signOut() {
        authRepository.signOut()
        _uiState.value = AppUiState()
    }

    fun updateProfile(username: String, bio: String, imageUri: Uri?) {
        viewModelScope.launch {
            val userId = _uiState.value.currentUser?.id ?: return@launch
            _uiState.value = _uiState.value.copy(isLoading = true)

            try {
                var profilePictureUrl: String? = null

                if (imageUri != null) {
                    val uploadResult = userRepository.uploadProfilePicture(userId, imageUri)
                    uploadResult.onSuccess { url ->
                        profilePictureUrl = url
                    }
                }

                val updateRequest = UpdateUserRequest(
                    username = username,
                    bio = bio,
                    profilePictureUrl = profilePictureUrl
                )

                val result = userRepository.updateUser(userId, updateRequest)
                result.onSuccess {
                    loadCurrentUser(userId)
                    _uiState.value = _uiState.value.copy(isLoading = false)
                }.onFailure { e ->
                    _uiState.value = _uiState.value.copy(
                        isLoading = false,
                        errorMessage = "Failed to update profile: ${e.message}"
                    )
                }
            } catch (e: Exception) {
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    errorMessage = "Error: ${e.message}"
                )
            }
        }
    }

    fun updateLocation() {
        viewModelScope.launch {
            try {
                println("DEBUG: Starting location update...")

                if (!locationHelper.hasLocationPermission()) {
                    println("DEBUG: Location permission not granted - using default location")
                    // Use a default location (e.g., San Francisco)
                    val defaultLocation = Location("default").apply {
                        latitude = 37.7749
                        longitude = -122.4194
                    }
                    _uiState.value = _uiState.value.copy(currentLocation = defaultLocation)
                    fetchNearbySecrets(defaultLocation.latitude, defaultLocation.longitude)
                    return@launch
                }

                if (!locationHelper.isLocationEnabled()) {
                    println("DEBUG: Location services not enabled - using default location")
                    // Use a default location
                    val defaultLocation = Location("default").apply {
                        latitude = 37.7749
                        longitude = -122.4194
                    }
                    _uiState.value = _uiState.value.copy(currentLocation = defaultLocation)
                    fetchNearbySecrets(defaultLocation.latitude, defaultLocation.longitude)
                    return@launch
                }

                println("DEBUG: Fetching location...")
                val location = locationHelper.getCurrentLocation()

                if (location == null) {
                    println("DEBUG: Location is null - using default location")
                    // Use a default location
                    val defaultLocation = Location("default").apply {
                        latitude = 37.7749
                        longitude = -122.4194
                    }
                    _uiState.value = _uiState.value.copy(currentLocation = defaultLocation)
                    fetchNearbySecrets(defaultLocation.latitude, defaultLocation.longitude)
                    return@launch
                }

                println("DEBUG:  Location obtained - Lat: ${location.latitude}, Lng: ${location.longitude}")
                _uiState.value = _uiState.value.copy(currentLocation = location)

                // Fetch nearby secrets with the obtained location
                fetchNearbySecrets(location.latitude, location.longitude)

            } catch (e: Exception) {
                println("DEBUG:  Location error - ${e.message}, using default location")
                e.printStackTrace()
                // Use default location on error
                val defaultLocation = Location("default").apply {
                    latitude = 37.7749
                    longitude = -122.4194
                }
                _uiState.value = _uiState.value.copy(currentLocation = defaultLocation)
                fetchNearbySecrets(defaultLocation.latitude, defaultLocation.longitude)
            }
        }
    }

    fun fetchNearbySecrets(latitude: Double, longitude: Double) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true)

            val result = secretRepository.getNearbySecrets(latitude, longitude)

            result.onSuccess { secrets ->
                val secretsWithDistance = secrets.map { secret ->
                    val distance = LocationHelper.calculateDistance(
                        latitude, longitude,
                        secret.latitude, secret.longitude
                    )
                    secret.copy(distance = distance)
                }

                _uiState.value = _uiState.value.copy(
                    secrets = secretsWithDistance,
                    isLoading = false
                )
            }.onFailure { e ->
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    errorMessage = "Failed to fetch secrets: ${e.message}"
                )
            }
        }
    }

    fun createSecret(text: String, imageUri: Uri?, isAnonymous: Boolean, mood: String? = null, category: String? = null, hashtags: String? = null) {
        viewModelScope.launch {
            val location = _uiState.value.currentLocation
            val user = _uiState.value.currentUser

            if (location == null) {
                _uiState.value = _uiState.value.copy(errorMessage = "Location not available")
                return@launch
            }

            if (user == null) {
                _uiState.value = _uiState.value.copy(errorMessage = "User not authenticated")
                return@launch
            }

            _uiState.value = _uiState.value.copy(isLoading = true)

            val result = secretRepository.createSecret(
                text = text,
                imageUri = imageUri,
                latitude = location.latitude,
                longitude = location.longitude,
                username = user.username,
                userProfilePicture = user.profilePictureUrl,
                isAnonymous = isAnonymous,
                mood = mood,
                category = category,
                hashtags = hashtags
            )

            result.onSuccess {
                _uiState.value = _uiState.value.copy(isLoading = false)
                fetchNearbySecrets(location.latitude, location.longitude)
            }.onFailure { e ->
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    errorMessage = "Failed to post secret: ${e.message}"
                )
            }
        }
    }

    fun toggleLike(secret: Secret) {
        viewModelScope.launch {
            val user = _uiState.value.currentUser ?: return@launch

            val result = secretRepository.toggleLike(secret.id, user.username)
            result.onSuccess { isLiked ->
                // Update the secret in the list
                val updatedSecrets = _uiState.value.secrets.map { s ->
                    if (s.id == secret.id) {
                        s.copy(
                            isLikedByCurrentUser = isLiked,
                            likeCount = if (isLiked) s.likeCount + 1 else maxOf(0, s.likeCount - 1)
                        )
                    } else s
                }
                _uiState.value = _uiState.value.copy(secrets = updatedSecrets)

                // Update selected secret if it's the same
                if (_uiState.value.selectedSecret?.id == secret.id) {
                    loadSecretDetails(secret.id)
                }
            }
        }
    }

    fun selectSecret(secret: Secret?) {
        _uiState.value = _uiState.value.copy(selectedSecret = secret)
        secret?.let { loadSecretDetails(it.id) }
    }

    fun loadSecretDetails(secretId: String) {
        viewModelScope.launch {
            val commentsResult = secretRepository.getComments(secretId)
            val likesResult = secretRepository.getLikes(secretId)

            commentsResult.onSuccess { comments ->
                _uiState.value = _uiState.value.copy(selectedSecretComments = comments)
            }

            likesResult.onSuccess { likes ->
                _uiState.value = _uiState.value.copy(selectedSecretLikes = likes)
            }
        }
    }

    fun addComment(secretId: String, text: String) {
        viewModelScope.launch {
            val user = _uiState.value.currentUser ?: return@launch

            println("DEBUG: Adding comment for secret $secretId")

            val result = secretRepository.addComment(
                secretId = secretId,
                text = text,
                username = user.username,
                userProfilePicture = user.profilePictureUrl
            )

            result.onSuccess { newComment ->
                println("DEBUG: Comment added successfully: ${newComment.text}")

                // Add small delay to ensure Firebase propagates the comment
                kotlinx.coroutines.delay(800)

                println("DEBUG: Reloading secret details...")
                // Reload comments and likes
                val commentsResult = secretRepository.getComments(secretId)
                val likesResult = secretRepository.getLikes(secretId)

                commentsResult.onSuccess { comments ->
                    println("DEBUG: Loaded ${comments.size} comments")
                    _uiState.value = _uiState.value.copy(selectedSecretComments = comments)
                }

                likesResult.onSuccess { likes ->
                    _uiState.value = _uiState.value.copy(selectedSecretLikes = likes)
                }

                // Update comment count in the secrets list
                val updatedSecrets = _uiState.value.secrets.map { s ->
                    if (s.id == secretId) {
                        s.copy(commentCount = s.commentCount + 1)
                    } else s
                }

                // Update the selected secret with new comment count
                val updatedSelectedSecret = _uiState.value.selectedSecret?.let { secret ->
                    if (secret.id == secretId) {
                        secret.copy(commentCount = secret.commentCount + 1)
                    } else secret
                }

                _uiState.value = _uiState.value.copy(
                    secrets = updatedSecrets,
                    selectedSecret = updatedSelectedSecret
                )

                println("DEBUG: UI state updated with ${_uiState.value.selectedSecretComments.size} comments")
            }.onFailure { e ->
                println("DEBUG: Failed to add comment: ${e.message}")
                _uiState.value = _uiState.value.copy(
                    errorMessage = "Failed to add comment: ${e.message}"
                )
            }
        }
    }

    fun loadMySecrets() {
        viewModelScope.launch {
            val userId = _uiState.value.currentUser?.id ?: return@launch
            _uiState.value = _uiState.value.copy(isLoading = true)

            val result = secretRepository.getUserSecrets(userId)
            result.onSuccess { secrets ->
                _uiState.value = _uiState.value.copy(
                    mySecrets = secrets,
                    isLoading = false
                )
            }.onFailure { e ->
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    errorMessage = "Failed to load your secrets: ${e.message}"
                )
            }
        }
    }

    fun clearError() {
        _uiState.value = _uiState.value.copy(errorMessage = null)
    }
}

package com.secretspaces32.android.viewmodel

import android.content.Context
import android.location.Location
import android.net.Uri
import androidx.lifecycle.ViewModel
